import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'AngularTempleteDrivenForm';


}

export class Country{
  id;
  name;

  constructor(id,name){
    this.id=id;
    this.name=name;
  }
}
